targets.expr:
histogram_quantile(
  0.95,
  sum by (le, deployment_environment_name, service_namespace, service_name) (
    info(rate(http_server_request_duration_seconds_bucket[$__rate_interval]), {deployment_environment_name=~"$deployment_environment_name", service_namespace=~"$service_namespace", service_name="$service_name"})
  )
)

histogram_quantile(
  0.95,
  sum by (le, deployment_environment_name, service_namespace, service_name) (
    info(rate(rpc_server_duration_milliseconds_bucket[$__rate_interval]), {deployment_environment_name=~"$deployment_environment_name", service_namespace=~"$service_namespace", service_name="$service_name"}) / 1000
  )
)

sum by (service_instance_id_host_name) (
  label_join(
    (
      sum by (service_instance_id, host_name) (
         rate(system_cpu_time_seconds_total{state!="idle"}[$__rate_interval])
      )
      * on(host_name) group_left(service_instance_id)
      target_info{service_namespace=~"$service_namespace", service_name="${service_name}"}
    ),
    "service_instance_id_host_name",
    " / ",
    "service_instance_id",
    "host_name"
  )
)

sum by (service_instance_id_host_name) (
  label_join(
    (
      (
        sum by (service_instance_id, host_name) (
          system_memory_usage_bytes{state!="free"}
        )
        /
        sum by (service_instance_id, host_name) (
          system_memory_usage_bytes
        )
      )
      * on(host_name) group_left(service_instance_id) (
        target_info{service_namespace=~"$service_namespace", service_name="${service_name}"}
      )
    ),
    "service_instance_id_host_name",
    " / ",
    "service_instance_id",
    "host_name"
  )
)
